L.RotatedMarker=L.Marker.extend({options:{angle:0},_setPos:function(e){L.Marker.prototype._setPos.call(this,e);if(L.DomUtil.TRANSFORM){this._icon.style[L.DomUtil.TRANSFORM]+=" rotate("+this.options.angle+"deg)"}else if(L.Browser.ie){var t=this.options.angle*L.LatLng.DEG_TO_RAD,n=Math.cos(t),r=Math.sin(t);this._icon.style.filter+=" progid:DXImageTransform.Microsoft.Matrix(sizingMethod='auto expand', M11="+n+", M12="+ -r+", M21="+r+", M22="+n+")"}}});L.rotatedMarker=function(e,t){return new L.RotatedMarker(e,t)}