function initializeScreens(){picsOnScreenSet={};for(var e=0;e<numTotalPics;e++){picsOnScreenSet[e]=false}var t=[0,1,2,3,4,5,6,7];t=shuffle(t);t.push(8);cachedImageNumber=getRandomPictureNumberNotCurrentlyVisible();cacheImage(cachedImageNumber);var n=0;var r=$(".non-center-screen");r.push($(".screen-5"));_.each(r,function(e){var r=Math.floor(t[n]*1e3);setTimeout(function(){changePicture(e)},r);n++})}function onResize(){var e=Math.floor(($(window).height()-$(".footer").height()-padding*6)/3);var t=Math.floor(e*heightToWidthRatio);var n=Math.floor(t*3+padding*4);$(".why-main-container").width(n+"px");$(".screen").width(t+"px");$(".screen").height(e+"px")}function showThanks(){$(".screen-5").animate({opacity:"1.0"},1e3)}function changePicture(e){$(e).animate({opacity:"0.0"},1e3);window.setTimeout(function(){var t=Number($(e).attr("pic-number"));picsOnScreenSet[t]=false;var n="/static/img/why/"+cachedImageNumber+".jpg";$(e).css("background-image","url("+n+")");$(e).attr("pic-number",cachedImageNumber);var r=getRandomPictureNumberNotCurrentlyVisible();cacheImage(r)},1e3);$(e).animate({opacity:"1.0"},1e3);window.setTimeout(function(){changePicture(e)},9e3)}function cacheImage(e){cachedImage=new Image;cachedImage.src="/static/img/why/"+e+".jpg";cachedImageNumber=e;picsOnScreenSet[e]=true}function getRandomPictureNumberNotCurrentlyVisible(){while(true){var e=Math.floor(Math.random()*numTotalPics);if(picsOnScreenSet[e]==false){return e}else{console.log("picture "+e+" is already on the screen... trying again.")}}}function shuffle(e){var t=e.length,n,r;while(t){r=Math.floor(Math.random()*t--);n=e[t];e[t]=e[r];e[r]=n}return e}var maxScreenWidth;var maxScreenHeight;var padding=10;var heightToWidthRatio=7/5;var numTotalPics=488;var picsOnScreenSet;var cachedImageNumber=0;var cachedImage;$(function(){onResize();showThanks();initializeScreens();$(window).resize(function(){onResize()})})